require(['src/util/pouchtovar'], function(pouchtovar) {
    Promise.all([pouchtovar.pouchToVar('external_infos', 'molfile'), pouchtovar.pouchToVar('external_infos', 'smiles')]).then(function(value) {
        var molfile = value[0];
        var smiles = value[1];


        if(!molfile._rev && !smiles._rev) {
            console.log('not available');
            return;
        }
        var useMolfile = (molfile._rev && !smiles._rev) || (molfile._rev && smiles._rev && molfile.timestamp > smiles.timestamp);

        if(useMolfile) {
            console.log('use molfile', molfile);
            $.ajax({
                url: 'http://script.epfl.ch/script/Service/20140826/IXnW4ymIPf',
                type: 'POST',
                data: { molfile: molfile.get() },
                dataType: 'json'
            }).done(function(val) {
                console.log('checked molfile:', val);
                var checkedMolfile = DataObject.check({type:'mol2d', value: val.molfile});
                API.createData('molfile', checkedMolfile);
                API.createData('molfile-ext', checkedMolfile);
            });




        }
        else {
            console.log('use smiles');
            $.ajax({
                url: 'http://www.nmrdb.org/service/smiles2molfile',
                type: 'POST',
                data: {smiles: smiles.get()},
                dataType: 'json'
            }).done(function(val){
                var molfile = DataObject.check({type:'mol2d', value: val.molfile});
                API.createData('molfile', molfile);
                API.createData('molfile-ext', molfile);
            })
        }
    });


    console.log(pouchtovar);
});
